 1	/**
 2	 * Async Handler Utility
 3	 * Wraps async functions to catch errors and pass them to Express error handler
 4	 */
 5	
 6	export const asyncHandler = (fn) => {
 7	 return (req, res, next) => {
 8	 Promise.resolve(fn(req, res, next)).catch(next);
 9	 };
 10	};
