Path: src/utils/error handling/globalHandler.js
...
 5	
 6	export const globalHandler = (error, req, res, next) => {
 7	 // Default error status and message
 8	 let statusCode = error.cause || 500;
 9	 let message = error.message || 'Internal Server Error';
 10	
 11	 // Handle specific error types
 12	 if (error.name === 'ValidationError') {
 13	 statusCode = 400;
 14	 message = error.message;
 15	 }
 16	
 17	 if (error.name === 'JsonWebTokenError') {
 18	 statusCode = 401;
 19	 message = 'Invalid token';
 20	 }
 21	
 22	 if (error.name === 'TokenExpiredError') {
 23	 statusCode = 401;
 24	 message = 'Token expired';
 25	 }
 26	
 27	 if (error.name === 'PrismaClientKnownRequestError') {
 28	 if (error.code === 'P2002') {
 29	 statusCode = 409;
 30	 message = 'Duplicate entry';
 31	 } else if (error.code === 'P2025') {
 32	 statusCode = 404;
 33	 message = 'Record not found';
 34	 }
 35	 }
 36	
 37	 // Log error for debugging (in development)
 38	 if (process.env.NODE_ENV === 'development') {
 39	 console.error('Error:', error);
 40	 }
 41	
 42	 // Send error response
 43	 res.status(statusCode).json({
 44	 success: false,
 45	 message,
 46	 ...(process.env.NODE_ENV === 'development' && { stack: error.stack })
 47	 });
 48	};
