 97	 * @desc Get all products with pagination, search, and filtering
 98	 * @route GET /api/products
 99	 * @access Private
 100	 */
 101	export const getProducts = asyncHandler(async (req, res) => {
 102	 const filters = {
 103	 page: parseInt(req.query.page) || 1,
 104	 limit: parseInt(req.query.limit) || 20,
 105	 search: req.query.search || '',
 106	 category: req.query.category ? parseInt(req.query.category) : undefined,
 107	 supplierId: req.query.supplierId || undefined,
 108	 inStock: req.query.inStock !== undefined ? req.query.inStock === 'true' : undefined,
 109	 sort: req.query.sort || 'CreatedDate',
 110	 order: req.query.order || 'desc'
 111	 };
 112	
 113	 const result = await getProductsService(filters);
 114	 
 115	 // Map products to response format
 116	 const products = result.products.map(mapProductToResponse);
 117	
 118	 res.status(200).json({
 119	 success: true,
 120	 message: 'Products retrieved successfully',
 121	 data: products,
 122	 pagination: {
 123	 currentPage: result.pagination.page,
 124	 totalPages: result.pagination.pages,
 125	 totalItems: result.pagination.total,
 126	 itemsPerPage: result.pagination.limit,
 127	 hasNextPage: result.pagination.page < result.pagination.pages,
 128	 hasPreviousPage: result.pagination.page > 1
 129	 }
 130	 });
 131	});
 132	
 133	/**
 134	 * @desc Get single product by ID
 135	 * @route GET /api/products/:id
 136	 * @access Private
 137	 */
 138	export const getProduct = asyncHandler(async (req, res) => {
 139	 const { id } = req.params;
 140	 const productId = parseInt(id);
 141	
 142	 const product = await getProductByIdService(productId);
 143	
 144	 if (!product) {
 145	 return res.status(404).json({
 146	 success: false,
 147	 message: 'Product not found'
 148	 });
 149	 }
 150	
 151	 res.status(200).json({
 152	 success: true,
 153	 message: 'Product retrieved successfully',
 154	 data: mapProductToResponse(product)
 155	 });
 156	});
 157	
 158	/**
 159	 * @desc Create new product
 160	 * @route POST /api/products
 161	 * @access Private
 162	 */
 163	export const createProduct = asyncHandler(async (req, res) => {
 164	 try {
 165	 const product = await createProductService(req.body);
 166	
 167	 res.status(201).json({
 168	 success: true,
 169	 message: 'Product created successfully',
 170	 data: mapProductToResponse(product)
 171	 });
 172	 } catch (error) {
 173	 if (error.message === 'Category not found') {
 174	 return res.status(404).json({
 175	 success: false,
 176	 message: 'Category not found'
 177	 });
 178	 }
 179	 if (error.message === 'Supplier not found') {
 180	 return res.status(404).json({
 181	 success: false,
 182	 message: 'Supplier not found'
 183	 });
 184	 }
 185	 if (error.message === 'Customer not found') {
 186	 return res.status(404).json({
 187	 success: false,
 188	 message: 'Customer not found'
 189	 });
 190	 }
 191	 throw error;
 192	 }
 193	});
