 117	/**
 118	 * @desc Get single supplier by ID
 119	 * @route GET /api/suppliers/:id
 120	 * @access Private
 121	 */
 122	export const getSupplier = asyncHandler(async (req, res) => {
 123	 const { id } = req.params;
 124	
 125	 const supplier = await getSupplierByIdService(id);
 126	
 127	 if (!supplier) {
 128	 return res.status(404).json({
 129	 success: false,
 130	 message: 'Supplier not found'
 131	 });
 132	 }
 133	
 134	 res.status(200).json({
 135	 success: true,
 136	 message: 'Supplier retrieved successfully',
 137	 data: mapSupplierToResponse(supplier)
 138	 });
 139	});
 140	
 141	/**
 142	 * @desc Get supplier products
 143	 * @route GET /api/suppliers/:id/products
 144	 * @access Private
 145	 */
 146	export const getSupplierProducts = asyncHandler(async (req, res) => {
 147	 const { id } = req.params;
 148	 const filters = {
 149	 page: parseInt(req.query.page) || 1,
 150	 limit: parseInt(req.query.limit) || 10
 151	 };
 152	
 153	 try {
 154	 const result = await getSupplierProductsService(id, filters);
 155	 
 156	 // Map products to response format
 157	 const products = result.products.map(mapProductToResponse);
 158	
 159	 res.status(200).json({
 160	 success: true,
 161	 message: 'Supplier products retrieved successfully',
 162	 data: {
 163	 products: products,
 164	 pagination: {
 165	 currentPage: result.pagination.page,
 166	 totalPages: result.pagination.pages,
 167	 totalItems: result.pagination.total,
 168	 itemsPerPage: result.pagination.limit,
 169	 hasNextPage: result.pagination.page < result.pagination.pages,
 170	 hasPreviousPage: result.pagination.page > 1
 171	 }
 172	 }
 173	 });
 174	 } catch (error) {
 175	 if (error.message === 'Supplier not found') {
 176	 return res.status(404).json({
 177	 success: false,
 178	 message: 'Supplier not found'
 179	 });
 180	 }
 181	 throw error;
 182	 }
 183	});
