 146	 const order = await createOrderService(req.body);
 147	
 148	 res.status(201).json({
 149	 success: true,
 150	 message: 'Order created successfully',
 151	 data: mapOrderToResponse(order)
 152	 });
 153	 } catch (error) {
 154	 if (error.message === 'Customer not found') {
 155	 return res.status(404).json({
 156	 success: false,
 157	 message: 'Customer not found'
 158	 });
 159	 }
 160	 if (error.message === 'Supplier not found') {
 161	 return res.status(404).json({
 162	 success: false,
 163	 message: 'Supplier not found'
 164	 });
 165	 }
 166	 if (error.message.includes('Product with ID') && error.message.includes('not found')) {
 167	 return res.status(404).json({
 168	 success: false,
 169	 message: error.message
 170	 });
 171	 }
