 8	// Custom GUID validation function
 9	const isValidGuid = (value, helpers) => {
 10	 const guidRegex = /^[0-9a-f]{8}-[0-9a-f]{4}-[1-5][0-9a-f]{3}-[89ab][0-9a-f]{3}-[0-9a-f]{12}$/i;
 11	 if (!guidRegex.test(value)) {
 12	 return helpers.error('any.invalid', { message: 'Invalid GUID format' });
 13	 }
 14	 return value;
 15	};
