 3	export const validation = (schema, source = 'body')=>{
 4	 return (req,res,next)=>{
 5	 //data
 6	 let data;
 7	
 8	 // Select data source based on parameter
 9	 switch(source) {
 10	 case 'query':
 11	 data = req.query;
 12	 break;
 13	 case 'params':
 14	 data = req.params;
 15	 break;
 16	 case 'body':
 17	 default:
 18	 data = req.body;
 19	 break;
 20	 }
 21	
 22	 // Add file data if present
 23	 if(req.file||req.files?.length){
 24	 data = { ...data, file: req.file || req.files };
 25	 }
 26	
 27	 const results = schema.validate(data,{abortEarly: false})
 28	 // errors
 29	 if (results.error){
 30	 const messageList = results.error.details.map((obj)=>obj.message);
 31	 return next(new Error(messageList,{cause:400}))
 32	 }
 33	 return next();
 34	 }
 35	};
 36	 // for joi.custom(value,helper) 
