 17	const bootstrap = async (app, express) => {
 18	 
 19	 // Security Middlewares
 20	 app.use(helmet());
 21	 app.use(cors());
 22	 const limiter = rateLimit({
 23	 windowMs: 15 * 60 * 1000,
 24	 max: 100, 
 25	 message: "Too many requests from this IP, please try again later.",
 26	 headers: true
 27	 });
 28	 app.use(limiter);
 29	 
 30	 // parsing json data
 31	 app.use(express.json());
 32	
 33	 //APIs endpoints
 34	 app.use('/api/auth', authRoutes);
 35	 app.use('/api/dashboard', analyticsRoutes);
 36	 app.use('/api/users', customerRoutes);
 37	 app.use('/api/orders', orderRoutes);
 38	 app.use('/api/suppliers', supplierRoutes);
 39	
 40	 //handle unhandled routes/endpoints
 41	 app.use('*', (req, res) => {
 42	 return res.status(404).json({ message: 'Route not found' });
 43	 });
 44	
 45	 // global error handling 
 46	 app.use(globalHandler);
 47	}
