 4	
 5	export const isAuthenticated = async (req, res, next) => {
 6	 try {
 7	 const { authorization } = req.headers;
 8	
 9	 // Validate Authorization Header
 10	 if (!authorization) {
 11	 return next(new Error('Authorization header required', { cause: 401 }));
 12	 }
 13	
 14	 const [bearer, token] = authorization.split(' ');
 15	 if (bearer !== 'Bearer' || !token) {
 16	 return next(new Error('Invalid token format', { cause: 401 }));
 17	 }
 18	
 19	 // Verify and Decode Token
 20	 const decoded = verifyToken({ token });
 21	
 22	 if (!decoded.id) {
 23	 return next(new Error('Invalid token', { cause: 401 }));
 24	 }
 25	
 26	 // Fetch User from Database
 27	 const user = await prisma.users.findUnique({
 28	 where: { Id: decoded.id },
 29	 });
 30	 if (!user) {
 31	 return next(new Error('User not found', { cause: 404 }));
 32	 }
 33	 // Check Account Confirmation
 34	 if (!user.EmailConfirmed) {
 35	 return next(new Error('Email not activated', { cause: 400 }));
 36	 }
 37	
 38	 // Validate Token Freshness
 39	 if (decoded.securityStamp !== user.SecurityStamp) {
 40	 return next(new Error('Session expired due to security changes. Please login.', { cause: 401 }));
 41	 }
 42	
 43	 // // Validate if User is Banned
 44	 // if (user.isBlocked) {
 45	 // return next(new Error('User is banned from the system.', { cause: 403 }));
 46	 // }
 47	
 48	 // Attach User and Company (if exists) to Request
 49	 req.user = user;
 50	
 51	 next();
 52	 } catch (error) {
 53	 next(error);
 54	 }
 55	};
