 43	 whereClause.CategoryId = category;
 44	 }
 45	 
 46	 // Add supplier filter
 47	 if (supplierId) {
 48	 whereClause.SupplierId = supplierId;
 49	 }
 50	 
 51	 // Add stock filter
 52	 if (inStock !== undefined) {
 53	 if (inStock) {
 54	 whereClause.Stock = {
 55	 gt: 0
 56	 };
 57	 } else {
 58	 whereClause.OR = [
 59	 { Stock: { lte: 0 } },
 60	 { Stock: null }
 61	 ];
 62	 }
 63	 }
 64	 
