 242	/**
 243	 * Create new product
 244	 */
 245	export const createProductService = async (productData) => {
 246	 const { Attributes, Variants, ...productFields } = productData;
 247	 
 248	 // Verify category exists
 249	 if (productFields.CategoryId) {
 250	 const category = await prisma.categories.findUnique({
 251	 where: { ID: productFields.CategoryId, Deleted: false }
 252	 });
 253	 if (!category) {
 254	 throw new Error('Category not found');
 255	 }
 256	 }
 257	 
 258	 // Verify supplier exists if provided
 259	 if (productFields.SupplierId) {
 260	 const supplier = await prisma.suppliers.findUnique({
 261	 where: { Id: productFields.SupplierId }
 262	 });
 263	 if (!supplier) {
 264	 throw new Error('Supplier not found');
 265	 }
 266	 }
 267	 
 268	 // Verify customer exists if provided
 269	 if (productFields.CustomerId) {
 270	 const customer = await prisma.customer.findUnique({
 271	 where: { Id: productFields.CustomerId }
 272	 });
 273	 if (!customer) {
 274	 throw new Error('Customer not found');
 275	 }
 276	 }
 277	 
 278	 // Create product with related data
 279	 const product = await prisma.products.create({
 280	 data: {
 281	 ...productFields,
 282	 Deleted: false,
 283	 CreatedDate: new Date(),
 284	 // Create attributes if provided
 285	 ...(Attributes && Attributes.length > 0 && {
 286	 ProductAttribute: {
 287	 create: Attributes.map(attr => ({
 288	 Key: attr.Key,
 289	 Value: attr.Value,
 290	 Deleted: false,
 291	 CreatedDate: new Date()
 292	 }))
 293	 }
 294	 }),
 295	 // Create variants if provided
 296	 ...(Variants && Variants.length > 0 && {
 297	 ProductVariant: {
 298	 create: Variants.map(variant => ({
 299	 Name: variant.Name,
 300	 Type: variant.Type,
 301	 CustomPrice: variant.CustomPrice,
 302	 Stock: variant.Stock,
 303	 Deleted: false,
 304	 CreatedDate: new Date()
 305	 }))
 306	 }
 307	 })
 308	 },
 309	 include: {
 310	 Categories: true,
 311	 Suppliers: {
 312	 include: {
 313	 Users: {
 314	 select: {
 315	 Id: true,
 316	 Name: true,
 317	 Email: true
 318	 }
 319	 }
 320	 }
 321	 },
 322	 Customer: {
 323	 include: {
 324	 Users: {
 325	 select: {
 326	 Id: true,
 327	 Name: true,
 328	 Email: true
 329	 }
 330	 }
 331	 }
 332	 },
 333	 Images: {
 334	 where: { Deleted: false }
 335	 },
 336	 ProductAttribute: {
 337	 where: { Deleted: false }
 338	 },
 339	 ProductVariant: {
 340	 where: { Deleted: false }
 341	 }
 342	 }
 343	 });
 344	 
 345	 return product;
 346	};
