 348	/**
 349	 * Update existing product
 350	 */
 351	export const updateProductService = async (productId, updateData) => {
 352	 // Check if product exists
 353	 const existingProduct = await prisma.products.findUnique({
 354	 where: { ID: productId, Deleted: false }
 355	 });
 356	
 357	 if (!existingProduct) {
 358	 throw new Error('Product not found');
 359	 }
 360	
 361	 // Verify category exists if being updated
 362	 if (updateData.CategoryId) {
 363	 const category = await prisma.categories.findUnique({
 364	 where: { ID: updateData.CategoryId, Deleted: false }
 365	 });
 366	 if (!category) {
 367	 throw new Error('Category not found');
 368	 }
 369	 }
 370	
 371	 // Verify supplier exists if being updated
 372	 if (updateData.SupplierId) {
 373	 const supplier = await prisma.suppliers.findUnique({
 374	 where: { Id: updateData.SupplierId }
 375	 });
 376	 if (!supplier) {
 377	 throw new Error('Supplier not found');
 378	 }
 379	 }
 380	
 381	 // Verify customer exists if being updated
 382	 if (updateData.CustomerId) {
 383	 const customer = await prisma.customer.findUnique({
 384	 where: { Id: updateData.CustomerId }
 385	 });
 386	 if (!customer) {
 387	 throw new Error('Customer not found');
 388	 }
 389	 }
 390	
 391	 // Update product
 392	 const product = await prisma.products.update({
 393	 where: { ID: productId },
 394	 data: {
 395	 ...updateData,
 396	 UpdatedDate: new Date()
 397	 },
 398	 include: {
 399	 Categories: true,
 400	 Suppliers: {
 401	 include: {
 402	 Users: {
 403	 select: {
 404	 Id: true,
 405	 Name: true,
 406	 Email: true
 407	 }
 408	 }
 409	 }
 410	 },
 411	 Customer: {
 412	 include: {
 413	 Users: {
 414	 select: {
 415	 Id: true,
 416	 Name: true,
 417	 Email: true
 418	 }
 419	 }
 420	 }
 421	 },
 422	 Images: {
 423	 where: { Deleted: false }
 424	 },
 425	 ProductAttribute: {
 426	 where: { Deleted: false }
 427	 },
 428	 ProductVariant: {
 429	 where: { Deleted: false }
 430	 }
 431	 }
 432	 });
 433	
 434	 return product;
 435	};
