 492	/**
 493	 * Upload product images
 494	 */
 495	export const uploadProductImagesService = async (productId, imageUrls) => {
 496	 // Check if product exists
 497	 const existingProduct = await prisma.products.findUnique({
 498	 where: { ID: productId, Deleted: false }
 499	 });
 500	
 501	 if (!existingProduct) {
 502	 throw new Error('Product not found');
 503	 }
 504	
 505	 // Create image records
 506	 const images = await prisma.images.createMany({
 507	 data: imageUrls.map(url => ({
 508	 Url: url,
 509	 ProductId: productId,
 510	 Deleted: false,
 511	 CreatedDate: new Date()
 512	 }))
 513	 });
 514	
 515	 // Return the created image URLs
 516	 return imageUrls;
 517	};
