 436	 */
 437	export const deleteProductService = async (productId) => {
 438	 // Check if product exists
 439	 const existingProduct = await prisma.products.findUnique({
 440	 where: { ID: productId, Deleted: false }
 441	 });
 442	
 443	 if (!existingProduct) {
 444	 throw new Error('Product not found');
 445	 }
 446	
 447	 // Soft delete product and related data
 448	 await prisma.$transaction(async (tx) => {
 449	 // Delete product
 450	 await tx.products.update({
 451	 where: { ID: productId },
 452	 data: {
 453	 Deleted: true,
 454	 UpdatedDate: new Date()
 455	 }
 456	 });
 457	
 458	 // Delete related images
 459	 await tx.images.updateMany({
 460	 where: { ProductId: productId },
 461	 data: {
 462	 Deleted: true,
 463	 UpdatedDate: new Date()
 464	 }
 465	 });
 466	
 467	 // Delete related attributes
 468	 await tx.productAttribute.updateMany({
 469	 where: { ProductId: productId },
 470	 data: {
 471	 Deleted: true,
 472	 UpdatedDate: new Date()
 473	 }
 474	 });
 475	
 476	 // Delete related variants
 477	 await tx.productVariant.updateMany({
 478	 where: { ProductId: productId },
 479	 data: {
 480	 Deleted: true,
 481	 UpdatedDate: new Date()
 482	 }
 483	 });
 484	 });
 485	
 486	 return true;
 487	};
 488	
 489	/**
 490	 * Upload product images
 491	 */
 492	export const uploadProductImagesService = async (productId, imageUrls) => {
 493	 // Check if product exists
 494	 const existingProduct = await prisma.products.findUnique({
 495	 where: { ID: productId, Deleted: false }
 496	 });
 497	
 498	 if (!existingProduct) {
 499	 throw new Error('Product not found');
 500	 }
 501	
 502	 // Create image records
 503	 const images = await prisma.images.createMany({
 504	 data: imageUrls.map(url => ({
 505	 Url: url,
 506	 ProductId: productId,
 507	 Deleted: false,
 508	 CreatedDate: new Date()
 509	 }))
 510	 });
 511	
 512	 // Return the created image URLs
 513	 return imageUrls;
 514	};
