 437	export const deleteProductService = async (productId) => {
 438	 // Check if product exists
 439	 const existingProduct = await prisma.products.findUnique({
 440	 where: { ID: productId, Deleted: false }
 441	 });
 442	
 443	 if (!existingProduct) {
 444	 throw new Error('Product not found');
 445	 }
 446	
 447	 // Soft delete product and related data
 448	 await prisma.$transaction(async (tx) => {
 449	 // Delete product
 450	 await tx.products.update({
 451	 where: { ID: productId },
 452	 data: {
 453	 Deleted: true,
 454	 UpdatedDate: new Date()
 455	 }
 456	 });
 457	
 458	 // Delete related images
 459	 await tx.images.updateMany({
 460	 where: { ProductId: productId },
 461	 data: {
 462	 Deleted: true,
 463	 UpdatedDate: new Date()
 464	 }
 465	 });
 466	
 467	 // Delete related attributes
 468	 await tx.productAttribute.updateMany({
 469	 where: { ProductId: productId },
 470	 data: {
 471	 Deleted: true,
 472	 UpdatedDate: new Date()
 473	 }
 474	 });
 475	
 476	 // Delete related variants
