 594	
 595	/**
 596	 * Upload product images
 597	 */
 598	export const uploadProductImagesService = async (productId, imageUrls) => {
 599	 // Check if product exists
 600	 const existingProduct = await prisma.products.findUnique({
 601	 where: { ID: productId, Deleted: false }
 602	 });
 603	
 604	 if (!existingProduct) {
 605	 throw new Error('Product not found');
 606	 }
 607	
 608	 // Create image records
 609	 const images = await prisma.images.createMany({
 610	 data: imageUrls.map(url => ({
 611	 Url: url,
 612	 ProductId: productId,
 613	 Deleted: false,
 614	 CreatedDate: new Date()
 615	 }))
 616	 });
 617	
 618	 // Return the created image URLs
 619	 return imageUrls;
 620	};
