 269	/**
 270	 * @desc Upload product images
 271	 * @route POST /api/products/:id/images
 272	 * @access Private
 273	 */
 274	export const uploadProductImages = asyncHandler(async (req, res) => {
 275	 const { id } = req.params;
 276	 const productId = parseInt(id);
 277	
 278	 // Check if files were uploaded
 279	 if (!req.files || req.files.length === 0) {
 280	 return res.status(400).json({
 281	 success: false,
 282	 message: 'No images uploaded'
 283	 });
 284	 }
 285	
 286	 // Validate file count (max 10 files)
 287	 if (req.files.length > 10) {
 288	 return res.status(400).json({
 289	 success: false,
 290	 message: 'Maximum 10 images allowed per upload'
 291	 });
 292	 }
 293	
 294	 try {
 295	 // Upload images to Cloudinary
 296	 const uploadPromises = req.files.map(async (file, index) => {
 297	 try {
 298	 // Create a unique public ID for each image
 299	 const publicId = `product_${productId}_${Date.now()}_${index}`;
 300	
 301	 // Upload to Cloudinary
 302	 const result = await uploadToCloudinary(file.buffer, 'products', publicId);
 303	
 304	 return {
 305	 url: result.secure_url,
 306	 publicId: result.public_id,
 307	 originalName: file.originalname
 308	 };
 309	 } catch (uploadError) {
 310	 console.error(`Error uploading file ${file.originalname}:`, uploadError);
 311	 throw new Error(`Failed to upload ${file.originalname}: ${uploadError.message}`);
 312	 }
 313	 });
 314	
 315	 // Wait for all uploads to complete
 316	 const uploadResults = await Promise.all(uploadPromises);
 317	
 318	 // Extract URLs for database storage
 319	 const imageUrls = uploadResults.map(result => result.url);
 320	
 321	 // Save image URLs to database
 322	 const uploadedUrls = await uploadProductImagesService(productId, imageUrls);
 323	
 324	 res.status(200).json({
 325	 success: true,
 326	 message: 'Images uploaded successfully',
 327	 data: {
 328	 imageUrls: uploadedUrls,
 329	 uploadDetails: uploadResults.map(result => ({
 330	 url: result.url,
 331	 publicId: result.publicId,
 332	 originalName: result.originalName
 333	 }))
 334	 }
 335	 });
 336	 } catch (error) {
 337	 if (error.message === 'Product not found') {
 338	 return res.status(404).json({
 339	 success: false,
 340	 message: 'Product not found'
 341	 });
 342	 }
