 1	import multer from "multer";
 2	
 3	export const fileValidations = {
 4	 Image: ['jpg', 'jpeg', 'png', 'gif', 'webp'],
 5	 Document: ['pdf', 'doc', 'docx', 'xls', 'xlsx', 'ppt', 'pptx']
 6	};
 7	
 8	export const uploadCloudFile = (extensions = fileValidations.Image) => {
 9	 // Use memory storage to get file buffer for Cloudinary upload
 10	 const storage = multer.memoryStorage();
 11	
 12	 const fileFilter = (req, file, cb) => {
 13	 const fileExtension = file.originalname.split('.').pop().toLowerCase();
 14	 if (extensions.includes(fileExtension)) {
 15	 cb(null, true);
 16	 } else {
 17	 cb(new Error(`Invalid file format! Allowed formats: ${extensions.join(', ')}`), false);
 18	 }
 19	 };
 20	
 21	 // Set file size limit (5MB per file)
 22	 const limits = {
 23	 fileSize: 5 * 1024 * 1024 // 5MB
 24	 };
 25	
 26	 return multer({
 27	 storage,
 28	 fileFilter,
 29	 limits
 30	 });
 31	};...
