 122	 message: 'Products retrieved successfully',
 123	 data: products,
 124	 pagination: {
 125	 currentPage: result.pagination.page,
 126	 totalPages: result.pagination.pages,
 127	 totalItems: result.pagination.total,
 128	 itemsPerPage: result.pagination.limit,
