 357	/**
 358	 * @desc Delete specific product image
 359	 * @route DELETE /api/products/:productId/images/:imageId
 360	 * @access Private
 361	 */
 362	export const deleteProductImage = asyncHandler(async (req, res) => {
 363	 const { productId, imageId } = req.params;
 364	 const productIdInt = parseInt(productId);
 365	 const imageIdInt = parseInt(imageId);
 366	
 367	 try {
 368	 // Delete image from database (soft delete)
 369	 const deletedImageInfo = await deleteProductImageService(productIdInt, imageIdInt);
 370	
 371	 // Extract public ID from Cloudinary URL for deletion
 372	 let cloudinaryPublicId = null;
 373	 try {
 374	 // Extract public ID from Cloudinary URL
 375	 // URL format: https://res.cloudinary.com/cloud_name/image/upload/v1234567890/folder/public_id.ext
 376	 const urlParts = deletedImageInfo.imageUrl.split('/');
 377	 const uploadIndex = urlParts.indexOf('upload');
 378	 if (uploadIndex !== -1 && uploadIndex + 2 < urlParts.length) {
 379	 // Get the part after version (v1234567890)
 380	 const pathWithExtension = urlParts.slice(uploadIndex + 2).join('/');
 381	 // Remove file extension
 382	 cloudinaryPublicId = pathWithExtension.replace(/\.[^/.]+$/, '');
 383	 }
 384	 } catch (urlParseError) {
 385	 console.warn('Could not parse Cloudinary URL for deletion:', urlParseError.message);
 386	 }
 387	
 388	 // Attempt to delete from Cloudinary if we have a valid public ID
 389	 let cloudinaryDeleted = false;
 390	 if (cloudinaryPublicId) {
 391	 try {
 392	 const cloudinaryResult = await deleteFromCloudinary(cloudinaryPublicId);
 393	 cloudinaryDeleted = cloudinaryResult.result === 'ok';
 394	 console.log('Cloudinary deletion result:', cloudinaryResult);
 395	 } catch (cloudinaryError) {
 396	 console.error('Failed to delete image from Cloudinary:', cloudinaryError.message);
 397	 // Continue execution - database deletion was successful
 398	 }
 399	 }
 400	
 401	 res.status(200).json({
 402	 success: true,
 403	 message: 'Image deleted successfully',
 404	 data: {
 405	 imageId: deletedImageInfo.imageId,
 406	 productId: deletedImageInfo.productId,
 407	 imageUrl: deletedImageInfo.imageUrl,
 408	 cloudinaryDeleted: cloudinaryDeleted,
 409	 cloudinaryPublicId: cloudinaryPublicId
 410	 }
 411	 });
 412	 } catch (error) {
 413	 if (error.message === 'Product not found') {
 414	 return res.status(404).json({
 415	 success: false,
 416	 message: 'Product not found'
 417	 });
 418	 }
 419	 if (error.message === 'Image not found or does not belong to this product') {
 420	 return res.status(404).json({
 421	 success: false,
 422	 message: 'Image not found or does not belong to this product'
 423	 });
 424	 }
 425	 throw error;
 426	 }
 427	});
