 1	import { prisma } from '../../config/prismaClient.js';
 2	
 3	/**
 4	 * Products Service
 5	 * All database operations for products management
 6	 * Uses exact field names from the Products Prisma model
 7	 */
 8	
 9	/**
 10	 * Get products with filtering and pagination
 11	 */
 12	export const getProductsService = async (filters) => {
 13	 const { page, limit, search, category, supplierId, inStock, sort, order } = filters;
 14	 
 15	 // Calculate pagination
 16	 const skip = (page - 1) * limit;
 17	 
 18	 // Build where clause
 19	 const whereClause = {
 20	 Deleted: false
 21	 };
 22	 
 23	 // Add search filter (search in Name and SKU)
 24	 if (search && search.trim()) {
 25	 whereClause.OR = [
 26	 {
 27	 Name: {
 28	 contains: search.trim(),
 29	 mode: 'insensitive'
 30	 }
 31	 },
 32	 {
 33	 SKU: {
 34	 contains: search.trim(),
 35	 mode: 'insensitive'
 36	 }
 37	 }
 38	 ];
 39	 }
 40	 
 41	 // Add category filter
 42	 if (category) {
 43	 whereClause.CategoryId = category;
 44	 }
 45	 
 46	 // Add supplier filter
 47	 if (supplierId) {
 48	 whereClause.SupplierId = supplierId;
 49	 }
 50	 
 51	 // Add stock filter
 52	 if (inStock !== undefined) {
 53	 if (inStock) {
 54	 whereClause.Stock = {
 55	 gt: 0
 56	 };
 57	 } else {
 58	 whereClause.OR = [
 59	 { Stock: { lte: 0 } },
 60	 { Stock: null }
 61	 ];
 62	 }
 63	 }
 64	 
 65	 // Build order by clause
 66	 const orderBy = {};
 67	 orderBy[sort] = order;
 68	 
 69	 // Execute query
 70	 const [products, total] = await Promise.all([
 71	 prisma.products.findMany({
 72	 where: whereClause,
 73	 include: {
 74	 Categories: {
 75	 select: {
 76	 ID: true,
 77	 Name: true,
 78	 Description: true
 79	 }
 80	 },
 81	 Suppliers: {
 82	 include: {
 83	 Users: {
 84	 select: {
 85	 Id: true,
 86	 Name: true,
 87	 Email: true
 88	 }
 89	 }
 90	 }
 91	 },
 92	 Customer: {
 93	 include: {
 94	 Users: {
 95	 select: {
 96	 Id: true,
 97	 Name: true,
 98	 Email: true
 99	 }
 100	 }
 101	 }
 102	 },
 103	 Images: {
 104	 where: { Deleted: false },
 105	 select: {
 106	 ID: true,
 107	 Url: true
 108	 }
 109	 },
 110	 ProductAttribute: {
 111	 where: { Deleted: false },
 112	 select: {
 113	 ID: true,
 114	 Key: true,
 115	 Value: true
 116	 }
 117	 },
 118	 ProductVariant: {
 119	 where: { Deleted: false },
 120	 select: {
 121	 ID: true,
 122	 Name: true,
 123	 Type: true,
 124	 CustomPrice: true,
 125	 Stock: true
 126	 }
 127	 }
 128	 },
 129	 orderBy,
 130	 skip,
 131	 take: limit
 132	 }),
 133	 prisma.products.count({
 134	 where: whereClause
 135	 })
 136	 ]);
 137	 
 138	 return {
 139	 products,
 140	 pagination: {
 141	 page,
 142	 limit,
 143	 total,
 144	 pages: Math.ceil(total / limit)
 145	 }
 146	 };
 147	};
