 152	export const getProductByIdService = async (productId) => {
 153	 const product = await prisma.products.findUnique({
 154	 where: {
 155	 ID: productId,
 156	 Deleted: false
 157	 },
 158	 include: {
 159	 Categories: {
 160	 select: {
 161	 ID: true,
 162	 Name: true,
 163	 Description: true
 164	 }
 165	 },
 166	 Suppliers: {
 167	 include: {
 168	 Users: {
 169	 select: {
 170	 Id: true,
 171	 Name: true,
 172	 Email: true,
 173	 PhoneNumber: true
 174	 }
 175	 }
 176	 }
 177	 },
 178	 Customer: {
 179	 include: {
 180	 Users: {
 181	 select: {
 182	 Id: true,
 183	 Name: true,
 184	 Email: true
 185	 }
 186	 }
 187	 }
 188	 },
 189	 Images: {
 190	 where: { Deleted: false },
 191	 select: {
 192	 ID: true,
 193	 Url: true
 194	 }
 195	 },
 196	 ProductAttribute: {
 197	 where: { Deleted: false },
 198	 select: {
 199	 ID: true,
 200	 Key: true,
 201	 Value: true
 202	 }
 203	 },
 204	 ProductVariant: {
 205	 where: { Deleted: false },
 206	 select: {
 207	 ID: true,
 208	 Name: true,
 209	 Type: true,
 210	 CustomPrice: true,
 211	 Stock: true
 212	 }
 213	 },
 214	 Reviews: {
 215	 where: { Deleted: false },
 216	 include: {
 217	 Customer: {
 218	 include: {
 219	 Users: {
 220	 select: {
 221	 Id: true,
 222	 Name: true
 223	 }
 224	 }
 225	 }
 226	 }
 227	 },
 228	 select: {
