 439	 */
 440	export const deleteProductService = async (productId) => {
 441	 // Check if product exists
 442	 const existingProduct = await prisma.products.findUnique({
 443	 where: { ID: productId, Deleted: false }
 444	 });
 445	
 446	 if (!existingProduct) {
 447	 throw new Error('Product not found');
 448	 }
 449	
 450	 // Soft delete product and related data
 451	 await prisma.$transaction(async (tx) => {
 452	 // Delete product
 453	 await tx.products.update({
 454	 where: { ID: productId },
 455	 data: {
 456	 Deleted: true,
 457	 UpdatedDate: new Date()
 458	 }
 459	 });
 460	
 461	 // Delete related images
 462	 await tx.images.updateMany({
 463	 where: { ProductId: productId },
 464	 data: {
 465	 Deleted: true,
 466	 UpdatedDate: new Date()
 467	 }
 468	 });
 469	
 470	 // Delete related attributes
 471	 await tx.productAttribute.updateMany({
 472	 where: { ProductId: productId },
 473	 data: {
 474	 Deleted: true,
 475	 UpdatedDate: new Date()
 476	 }
 477	 });
 478	
 479	 // Delete related variants
 480	 await tx.productVariant.updateMany({
 481	 where: { ProductId: productId },
 482	 data: {
 483	 Deleted: true,
 484	 UpdatedDate: new Date()
 485	 }
 486	 });
 487	 });
 488	
 489	 return true;
