 9	const isValidId = (value, helpers) => {
 10	 if (typeof value !== 'string' || value.length === 0 || value.length > 450) {
 11	 return helpers.error('any.invalid', { message: 'Invalid ID format' });
 12	 }
 13	 return value;
 14	};
 15	
 16	const isValidGuid = (value, helpers) => {
