 348	export const updateProductService = async (productId, updateData) => {
 349	 // Check if product exists
 350	 const existingProduct = await prisma.products.findUnique({
 351	 where: { ID: productId, Deleted: false }
 352	 });
 353	
 354	 if (!existingProduct) {
 355	 throw new Error('Product not found');
 356	 }
 357	
 358	 // Verify category exists if being updated
 359	 if (updateData.CategoryId) {
 360	 const category = await prisma.categories.findUnique({
 361	 where: { ID: updateData.CategoryId, Deleted: false }
 362	 });
 363	 if (!category) {
 364	 throw new Error('Category not found');
 365	 }
 366	 }
