 368	 // Verify supplier exists if being updated
 369	 if (updateData.SupplierId) {
 370	 const supplier = await prisma.suppliers.findUnique({
 371	 where: { Id: updateData.SupplierId }
 372	 });
 373	 if (!supplier) {
 374	 throw new Error('Supplier not found');
 375	 }
 376	 }
