 378	 // Verify customer exists if being updated
 379	 if (updateData.CustomerId) {
 380	 const customer = await prisma.customer.findUnique({
 381	 where: { Id: updateData.CustomerId }
 382	 });
 383	 if (!customer) {
 384	 throw new Error('Customer not found');
 385	 }
 386	 }
