 37	 type: variant.Type,
 38	 price: parseFloat(variant.CustomPrice),
 39	 stock: variant.Stock
 40	 })) || [];
 41	
 42	 // Map category
 43	 const category = product.Categories ? {
 44	 id: product.Categories.ID,
 45	 name: product.Categories.Name,
 46	 description: product.Categories.Description
 47	 } : null;
