 159	/**
 160	 * @desc Create new product
 161	 * @route POST /api/products
 162	 * @access Private
 163	 */
 164	export const createProduct = asyncHandler(async (req, res) => {
 165	 try {
 166	 const product = await createProductService(req.body);
 167	
 168	 res.status(201).json({
 169	 success: true,
 170	 message: 'Product created successfully',
 171	 data: mapProductToResponse(product)
 172	 });
 173	 } catch (error) {
 174	 if (error.message === 'Category not found') {
 175	 return res.status(404).json({
 176	 success: false,
 177	 message: 'Category not found'
 178	 });
 179	 }
 180	 if (error.message === 'Supplier not found') {
 181	 return res.status(404).json({
 182	 success: false,
 183	 message: 'Supplier not found'
 184	 });
 185	 }
 186	 if (error.message === 'Customer not found') {
 187	 return res.status(404).json({
 188	 success: false,
 189	 message: 'Customer not found'
 190	 });
 191	 }
 192	 throw error;
 193	 }
 194	});
