Path: src/config/cloudinary.js
 1	import { v2 as cloudinary } from 'cloudinary';
 2	import dotenv from 'dotenv';
 3	
 4	dotenv.config();
 5	
 6	/**
 7	 * Cloudinary Configuration
 8	 * Configure cloudinary with environment variables
 9	 */
 10	cloudinary.config({
 11	 cloud_name: process.env.CLOUD_NAME,
 12	 api_key: process.env.CLOUD_API_KEY,
 13	 api_secret: process.env.CLOUD_API_SEC,
 14	 secure: true
 15	});
 16	
 17	/**
 18	 * Upload image to Cloudinary
 19	 * @param {Buffer} fileBuffer - File buffer from multer
 20	 * @param {string} folder - Cloudinary folder name
 21	 * @param {string} publicId - Optional public ID for the image
 22	 * @returns {Promise<Object>} Cloudinary upload result
 23	 */
 24	export const uploadToCloudinary = (fileBuffer, folder = 'products', publicId = null) => {
 25	 return new Promise((resolve, reject) => {
 26	 const uploadOptions = {
 27	 folder: folder,
 28	 resource_type: 'image',
 29	 quality: 'auto',
 30	 fetch_format: 'auto'
 31	 };
 32	
 33	 if (publicId) {
 34	 uploadOptions.public_id = publicId;
 35	 }
 36	
 37	 cloudinary.uploader.upload_stream(
 38	 uploadOptions,
 39	 (error, result) => {
 40	 if (error) {
 41	 reject(error);
 42	 } else {
 43	 resolve(result);
 44	 }
 45	 }
 46	 ).end(fileBuffer);
 47	 });
 48	};
 49	
 50	/**
 51	 * Delete image from Cloudinary
 52	 * @param {string} publicId - Public ID of the image to delete
 53	 * @returns {Promise<Object>} Cloudinary deletion result
 54	 */
 55	export const deleteFromCloudinary = (publicId) => {
 56	 return cloudinary.uploader.destroy(publicId);
 57	};
 58	
 59	/**
 60	 * Get optimized image URL
 61	 * @param {string} publicId - Public ID of the image
 62	 * @param {Object} options - Transformation options
 63	 * @returns {string} Optimized image URL
 64	 */
 65	export const getOptimizedImageUrl = (publicId, options = {}) => {
 66	 const defaultOptions = {
 67	 quality: 'auto',
 68	 fetch_format: 'auto'
 69	 };
 70	
 71	 return cloudinary.url(publicId, { ...defaultOptions, ...options });
 72	};
 73	
 74	export default cloudinary;
