 270	/**
 271	 * @desc Upload product images
 272	 * @route POST /api/products/:id/images
 273	 * @access Private
 274	 */
 275	export const uploadProductImages = asyncHandler(async (req, res) => {
 276	 const { id } = req.params;
 277	 const productId = parseInt(id);
 278	
 279	 // Check if files were uploaded
 280	 if (!req.files || req.files.length === 0) {
 281	 return res.status(400).json({
 282	 success: false,
 283	 message: 'No images uploaded'
 284	 });
 285	 }
 286	
 287	 // Validate file count (max 10 files)
 288	 if (req.files.length > 10) {
 289	 return res.status(400).json({
 290	 success: false,
 291	 message: 'Maximum 10 images allowed per upload'
 292	 });
 293	 }
 294	
 295	 try {
 296	 // Upload images to Cloudinary
 297	 const uploadPromises = req.files.map(async (file, index) => {
 298	 try {
 299	 // Create a unique public ID for each image
 300	 const publicId = `product_${productId}_${Date.now()}_${index}`;
 301	
 302	 // Upload to Cloudinary
 303	 const result = await uploadToCloudinary(file.buffer, 'products', publicId);
 304	
 305	 return {
 306	 url: result.secure_url,
 307	 publicId: result.public_id,
 308	 originalName: file.originalname
 309	 };
 310	 } catch (uploadError) {
 311	 console.error(`Error uploading file ${file.originalname}:`, uploadError);
 312	 throw new Error(`Failed to upload ${file.originalname}: ${uploadError.message}`);
 313	 }
 314	 });
 315	
 316	 // Wait for all uploads to complete
 317	 const uploadResults = await Promise.all(uploadPromises);
 318	
 319	 // Extract URLs for database storage
 320	 const imageUrls = uploadResults.map(result => result.url);
 321	
 322	 // Save image URLs to database
 323	 const uploadedUrls = await uploadProductImagesService(productId, imageUrls);
 324	
 325	 res.status(200).json({
 326	 success: true,
 327	 message: 'Images uploaded successfully',
 328	 data: {
 329	 imageUrls: uploadedUrls,
 330	 uploadDetails: uploadResults.map(result => ({
 331	 url: result.url,
 332	 publicId: result.publicId,
 333	 originalName: result.originalName
 334	 }))
 335	 }
 336	 });
 337	 } catch (error) {
 338	 if (error.message === 'Product not found') {
 339	 return res.status(404).json({
 340	 success: false,
 341	 message: 'Product not found'
 342	 });
 343	 }
 344	
 345	 // Handle upload errors
 346	 if (error.message.includes('Failed to upload')) {
 347	 return res.status(400).json({
 348	 success: false,
 349	 message: error.message
 350	 });
 351	 }
 352	
 353	 throw error;
 354	 }
 355	});
