 110	 const supplier = await prisma.users.findUnique({
 111	 where: {
 112	 Id: supplierId
 113	 },
 114	 include: {
 115	 Suppliers: true, // No need to include ActivityCategories
 116	 UserRoles: {
 117	 include: {
 118	 AspNetRoles: true
 119	 }
 120	 }
 121	 }
 122	 });
 123	
