 11	/**
 12	 * Get suppliers with pagination, search, and filtering
 13	 */
 14	export const getSuppliersService = async (filters) => {
 15	 const {
 16	 page = 1,
 17	 limit = 20,
 18	 search = '',
 19	 verificationStatus,
 20	 status,
 21	 sort = 'updatedAt',
 22	 order = 'desc'
 23	 } = filters;
 24	
 25	 const skip = (page - 1) * limit;
 26	 
 27	 // Build where clause
 28	 const whereClause = {
 29	 Suppliers: {
 30	 isNot: null // Only get users who are suppliers
 31	 }
 32	 };
 33	
 34	 // Add search filter (searches Name and Email)
 35	 if (search) {
 36	 whereClause.OR = [
 37	 {
 38	 Name: {
 39	 contains: search,
 40	 mode: 'insensitive'
 41	 }
 42	 },
 43	 {
 44	 Email: {
 45	 contains: search,
 46	 mode: 'insensitive'
 47	 }
 48	 }
 49	 ];
 50	 }
 51	
 52	 // Add verification status filter
 53	 if (verificationStatus) {
 54	 if (verificationStatus === 'verified') {
 55	 whereClause.EmailConfirmed = true;
 56	 } else if (verificationStatus === 'pending') {
 57	 whereClause.EmailConfirmed = false;
 58	 }
 59	 }
 60	
 61	 // Add status filter
 62	 if (status) {
 63	 if (status === 'active') {
 64	 whereClause.LockoutEnabled = false;
 65	 } else if (status === 'banned') {
 66	 whereClause.LockoutEnabled = true;
 67	 }
 68	 }
 69	
 70	 // Build order by clause
 71	 const orderBy = {};
 72	 if (sort === 'createdAt' || sort === 'updatedAt') {
 73	 // These fields don't exist in Users table, so we'll use a proxy
 74	 orderBy.Id = order; // Use Id as proxy for creation order
 75	 } else {
 76	 orderBy[sort] = order;
 77	 }
 78	
 79	 // Execute query
 80	 const [suppliers, total] = await Promise.all([
 81	 prisma.users.findMany({
 82	 where: whereClause,
 83	 include: {
 84	 Suppliers: true // No need to include ActivityCategories
 85	 },
 86	 orderBy,
 87	 skip,
 88	 take: limit
 89	 }),
 90	 prisma.users.count({
 91	 where: whereClause
 92	 })
 93	 ]);
 94	
 95	 return {
 96	 suppliers,
 97	 pagination: {
 98	 page,
 99	 limit,
 100	 total,
 101	 pages: Math.ceil(total / limit)
 102	 }
 103	 };
 104	};
 105	
 106	/**
 107	 * Get single supplier by ID
 108	 */
 109	export const getSupplierByIdService = async (supplierId) => {
 110	 const supplier = await prisma.users.findUnique({
 111	 where: {
 112	 Id: supplierId
 113	 },
 114	 include: {
 115	 Suppliers: true, // No need to include ActivityCategories
 116	 UserRoles: {
 117	 include: {
 118	 AspNetRoles: true
 119	 }
 120	 }
 121	 }
 122	 });
 123	
 124	 // Check if user is actually a supplier
 125	 if (supplier && !supplier.Suppliers) {
 126	 return null;
 127	 }
 128	
 129	 return supplier;
 130	};
