 132	/**
 133	 * Get supplier products with pagination
 134	 */
 135	export const getSupplierProductsService = async (supplierId, filters) => {
 136	 const {
 137	 page = 1,
 138	 limit = 10
 139	 } = filters;
 140	
 141	 const skip = (page - 1) * limit;
 142	
 143	 // First verify supplier exists
 144	 const supplier = await getSupplierByIdService(supplierId);
 145	 if (!supplier) {
 146	 throw new Error('Supplier not found');
 147	 }
 148	
 149	 // Build where clause for products
 150	 const whereClause = {
 151	 SupplierId: supplierId,
 152	 Deleted: false
 153	 };
 154	
 155	 // Execute query
 156	 const [products, total] = await Promise.all([
 157	 prisma.products.findMany({
 158	 where: whereClause,
 159	 include: {
 160	 Categories: true,
 161	 Images: {
 162	 where: { Deleted: false }
 163	 },
 164	 ProductAttribute: {
 165	 where: { Deleted: false }
 166	 },
 167	 ProductVariant: {
 168	 where: { Deleted: false }
 169	 }
 170	 },
 171	 orderBy: {
 172	 CreatedDate: 'desc'
 173	 },
 174	 skip,
 175	 take: limit
 176	 }),
 177	 prisma.products.count({
 178	 where: whereClause
 179	 })
 180	 ]);
 181	
 182	 return {
 183	 products,
 184	 pagination: {
 185	 page,
 186	 limit,
 187	 total,
 188	 pages: Math.ceil(total / limit)
 189	 }
 190	 };
 191	};
