 193	/**
 194	 * Create new supplier
 195	 */
 196	export const createSupplierService = async (supplierData) => {
 197	 const {
 198	 email,
 199	 password,
 200	 phone,
 201	 address,
 202	 contactPerson,
 203	 categories
 204	 } = supplierData;
 205	
 206	 // Check if email already exists
 207	 const existingUser = await prisma.users.findFirst({
 208	 where: {
 209	 Email: email
 210	 }
 211	 });
 212	
 213	 if (existingUser) {
 214	 throw new Error('Email already exists');
 215	 }
 216	
 217	 // Hash password
 218	 const hashedPassword = hashPassword(password);
 219	
 220	 // Generate unique ID
 221	 const userId = crypto.randomUUID();
 222	
 223	 // Create user and supplier in transaction
 224	 await prisma.$transaction(async (tx) => {
 225	 // Create user
 226	 const user = await tx.users.create({
 227	 data: {
 228	 Id: userId,
 229	 Name: contactPerson, // Use contactPerson as Name
 230	 Email: email,
 231	 UserName: email, // Use email as username
 232	 NormalizedUserName: email.toUpperCase(),
 233	 NormalizedEmail: email.toUpperCase(),
 234	 PasswordHash: hashedPassword,
 235	 SecurityStamp: crypto.randomUUID(),
 236	 ConcurrencyStamp: crypto.randomUUID(),
 237	 PhoneNumber: phone || null,
 238	 Address: address || null,
 239	 BusinessType: categories || null, // Store categories in BusinessType
 240	 EmailConfirmed: false, // Start as pending verification
 241	 PhoneNumberConfirmed: false,
 242	 TwoFactorEnabled: false,
 243	 LockoutEnabled: false,
 244	 AccessFailedCount: 0
 245	 }
 246	 });
 247	
 248	 // Create supplier profile
 249	 const supplier = await tx.suppliers.create({
 250	 data: {
 251	 Id: userId,
 252	 
 253	 }
 254	 });
 255	
 256	 return { user, supplier };
 257	 });
 258	
 259	 // Return the created supplier with category info
 260	 return await getSupplierByIdService(userId);
 261	};
