 305	/**
 306	 * @desc Delete supplier
 307	 * @route DELETE /api/suppliers/:id
 308	 * @access Private
 309	 */
 310	export const deleteSupplier = asyncHandler(async (req, res) => {
 311	 const { id } = req.params;
 312	
 313	 try {
 314	 await deleteSupplierService(id);
 315	
 316	 res.status(200).json({
 317	 success: true,
 318	 message: 'Supplier deleted successfully',
 319	 data: {
 320	 id: id,
 321	 status: 'deleted'
 322	 }
 323	 });
 324	 } catch (error) {
 325	 if (error.message === 'Supplier not found') {
 326	 return res.status(404).json({
 327	 success: false,
 328	 message: 'Supplier not found'
 329	 });
 330	 }
 331	 throw error;
 332	 }
 333	});
