 106	/**
 107	 * Get single supplier by ID
 108	 */
 109	export const getSupplierByIdService = async (supplierId) => {
 110	 const supplier = await prisma.users.findUnique({
 111	 where: {
 112	 Id: supplierId
 113	 },
 114	 include: {
 115	 Suppliers: true, // No need to include ActivityCategories
 116	 UserRoles: {
 117	 include: {
 118	 AspNetRoles: true
 119	 }
 120	 }
 121	 }
 122	 });
 123	
 124	 // Check if user is actually a supplier
 125	 if (supplier && !supplier.Suppliers) {
 126	 return null;
 127	 }
 128	
 129	 return supplier;
 130	};
