 262	
 263	/**
 264	 * Update supplier verification status
 265	 */
 266	export const updateSupplierVerificationStatusService = async (supplierId, verificationStatus) => {
 267	 // Check if supplier exists
 268	 const existingSupplier = await getSupplierByIdService(supplierId);
 269	 if (!existingSupplier) {
 270	 throw new Error('Supplier not found');
 271	 }
 272	
 273	 // Update verification status
 274	 await prisma.users.update({
 275	 where: { Id: supplierId },
 276	 data: {
 277	 EmailConfirmed: verificationStatus === 'verified',
 278	 SecurityStamp: crypto.randomUUID() // Invalidate existing tokens
 279	 }
 280	 });
 281	
 282	 return await getSupplierByIdService(supplierId);
 283	};
 284	
 285	/**
 286	 * Ban supplier
 287	 */
 288	export const banSupplierService = async (supplierId) => {
 289	 // Check if supplier exists
 290	 const existingSupplier = await getSupplierByIdService(supplierId);
 291	 if (!existingSupplier) {
 292	 throw new Error('Supplier not found');
 293	 }
 294	
 295	 // Ban supplier
 296	 await prisma.users.update({
 297	 where: { Id: supplierId },
 298	 data: {
 299	 LockoutEnabled: true,
 300	 LockoutEnd: new Date(Date.now() + 365 * 24 * 60 * 60 * 1000), // 1 year
 301	 SecurityStamp: crypto.randomUUID() // Invalidate existing tokens
 302	 }
 303	 });
 304	
 305	 return await getSupplierByIdService(supplierId);
 306	};
 307	
 308	/**
 309	 * Unban supplier
 310	 */
 311	export const unbanSupplierService = async (supplierId) => {
 312	 // Check if supplier exists
 313	 const existingSupplier = await getSupplierByIdService(supplierId);
 314	 if (!existingSupplier) {
 315	 throw new Error('Supplier not found');
 316	 }
 317	
 318	 // Unban supplier
 319	 await prisma.users.update({
 320	 where: { Id: supplierId },
 321	 data: {
 322	 LockoutEnabled: false,
 323	 LockoutEnd: null,
 324	 SecurityStamp: crypto.randomUUID() // Invalidate existing tokens
 325	 }
 326	 });
 327	
 328	 return await getSupplierByIdService(supplierId);
 329	};
 330	
 331	/**
 332	 * Delete supplier
 333	 */
 334	export const deleteSupplierService = async (supplierId) => {
 335	 // Check if supplier exists
 336	 const existingSupplier = await getSupplierByIdService(supplierId);
 337	 if (!existingSupplier) {
 338	 throw new Error('Supplier not found');
 339	 }
 340	
 341	 // Delete supplier (this will cascade delete the user due to FK constraint)
 342	 await prisma.suppliers.delete({
 343	 where: { Id: supplierId }
 344	 });
 345	
 346	 return true;
 347	};
