 539	
 540	/**
 541	 * Delete product (soft delete)
 542	 */
 543	export const deleteProductService = async (productId) => {
 544	 // Check if product exists
 545	 const existingProduct = await prisma.products.findUnique({
 546	 where: { ID: productId, Deleted: false }
 547	 });
 548	
 549	 if (!existingProduct) {
 550	 throw new Error('Product not found');
 551	 }
 552	
 553	 // Soft delete product and related data
 554	 await prisma.$transaction(async (tx) => {
 555	 // Delete product
 556	 await tx.products.update({
 557	 where: { ID: productId },
 558	 data: {
 559	 Deleted: true,
 560	 UpdatedDate: new Date()
 561	 }
 562	 });
 563	
 564	 // Delete related images
 565	 await tx.images.updateMany({
 566	 where: { ProductId: productId },
 567	 data: {
 568	 Deleted: true,
 569	 UpdatedDate: new Date()
 570	 }
 571	 });
 572	
 573	 // Delete related attributes
 574	 await tx.productAttribute.updateMany({
 575	 where: { ProductId: productId },
 576	 data: {
 577	 Deleted: true,
 578	 UpdatedDate: new Date()
 579	 }
 580	 });
 581	
 582	 // Delete related variants
 583	 await tx.productVariant.updateMany({
 584	 where: { ProductId: productId },
 585	 data: {
 586	 Deleted: true,
 587	 UpdatedDate: new Date()
 588	 }
 589	 });
 590	 });
 591	
 592	 return true;
 593	};
