 664	/**
 665	 * Delete specific product image by database ID (legacy function - kept for backward compatibility)
 666	 */
 667	export const deleteProductImageService = async (productId, imageId) => {
 668	 // Check if product exists
 669	 const existingProduct = await prisma.products.findUnique({
 670	 where: { ID: productId, Deleted: false }
 671	 });
 672	
 673	 if (!existingProduct) {
 674	 throw new Error('Product not found');
 675	 }
 676	
 677	 // Check if image exists and belongs to the product
 678	 const existingImage = await prisma.images.findFirst({
 679	 where: {
 680	 ID: imageId,
 681	 ProductId: productId,
 682	 Deleted: false
 683	 }
 684	 });
 685	
 686	 if (!existingImage) {
 687	 throw new Error('Image not found or does not belong to this product');
 688	 }
 689	
 690	 // Soft delete the image in database
 691	 const deletedImage = await prisma.images.update({
 692	 where: { ID: imageId },
 693	 data: {
 694	 Deleted: true,
 695	 UpdatedDate: new Date()
 696	 }
 697	 });
 698	
 699	 return {
 700	 imageId: deletedImage.ID,
 701	 imageUrl: deletedImage.Url,
 702	 productId: deletedImage.ProductId
 703	 };
 704	};
