 239	/**
 240	 * Create new product
 241	 */
 242	export const createProductService = async (productData) => {
 243	 const { Attributes, Variants, ...productFields } = productData;
 244	 
 245	 // Verify category exists
 246	 if (productFields.CategoryId) {
 247	 const category = await prisma.categories.findUnique({
 248	 where: { ID: productFields.CategoryId, Deleted: false }
 249	 });
 250	 if (!category) {
 251	 throw new Error('Category not found');
 252	 }
 253	 }
 254	 
 255	 // Verify supplier exists if provided
 256	 if (productFields.SupplierId) {
 257	 const supplier = await prisma.suppliers.findUnique({
 258	 where: { Id: productFields.SupplierId }
 259	 });
 260	 if (!supplier) {
 261	 throw new Error('Supplier not found');
 262	 }
 263	 }
...
 274	 
 275	 // Create product with related data
 276	 const product = await prisma.products.create({
 277	 data: {
 278	 ...productFields,
 279	 Deleted: false,
 280	 CreatedDate: new Date(),
 281	 // Create attributes if provided
 282	 ...(Attributes && Attributes.length > 0 && {
 283	 ProductAttribute: {
 284	 create: Attributes.map(attr => ({
 285	 Key: attr.Key,
 286	 Value: attr.Value,
 287	 Deleted: false,
 288	 CreatedDate: new Date()
 289	 }))
 290	 }
 291	 }),
 292	 // Create variants if provided
 293	 ...(Variants && Variants.length > 0 && {
 294	 ProductVariant: {
 295	 create: Variants.map(variant => ({
 296	 Name: variant.Name,
 297	 Type: variant.Type,
 298	 CustomPrice: variant.CustomPrice,
 299	 Stock: variant.Stock,
 300	 Deleted: false,
 301	 CreatedDate: new Date()
 302	 }))
 303	 }
 304	 })
 305	 },
...
 369	
 370	 // Verify supplier exists if being updated
 371	 if (productFields.SupplierId) {
 372	 const supplier = await prisma.suppliers.findUnique({
 373	 where: { Id: productFields.SupplierId }
 374	 });
 375	 if (!supplier) {
 376	 throw new Error('Supplier not found');
 377	 }
 378	 }
 379	
 380	 // Verify customer exists if being updated
 381	 if (productFields.CustomerId) {
 382	 const customer = await prisma.customer.findUnique({
 383	 where: { Id: productFields.CustomerId }
 384	 });
 385	 if (!customer) {
 386	 throw new Error('Customer not found');
 387	 }
 388	 }
 389	
 390	 // Use transaction to update product and related data
 391	 const product = await prisma.$transaction(async (tx) => {
 392	 // Update basic product fields
 393	 const updatedProduct = await tx.products.update({
 394	 where: { ID: productId },
 395	 data: {
 396	 ...productFields,
 397	 UpdatedDate: new Date()
 398	 }
 399	 });
...
