 220	/**
 221	 * Create new order with items
 222	 */
 223	export const createOrderService = async (orderData) => {
 224	 const {
 225	 CustomerId,
 226	 SupplierId,
 227	 items,
 228	 DeliveryFees = 0,
 229	 Discount = 0,
 230	 Notes = '',
 231	 PaymentMethod = 'cash'
 232	 } = orderData;
 233	
 234	 // Verify customer exists
 235	 const customer = await prisma.customer.findUnique({
 236	 where: { Id: CustomerId }
 237	 });
 238	 if (!customer) {
 239	 throw new Error('Customer not found');
 240	 }
 241	
 242	 // Verify supplier exists
 243	 const supplier = await prisma.suppliers.findUnique({
 244	 where: { Id: SupplierId }
 245	 });
 246	 if (!supplier) {
 247	 throw new Error('Supplier not found');
 248	 }
 249	
 250	 // Verify all products exist and calculate subtotal
 251	 let subTotal = 0;
 252	 const productChecks = await Promise.all(
 253	 items.map(async (item) => {
 254	 const product = await prisma.products.findUnique({
 255	 where: { ID: item.ProductId, Deleted: false }
 256	 });
 257	 if (!product) {
 258	 throw new Error(`Product with ID ${item.ProductId} not found`);
 259	 }
 260	 if (product.Stock < item.Quantity) {
 261	 throw new Error(`Insufficient stock for product ${product.Name}. Available: ${product.Stock}, Requested: ${item.Quantity}`);
 262	 }
 263	 subTotal += product.Price * item.Quantity;
 264	 return { product, quantity: item.Quantity };
 265	 })
 266	 );
 267	
 268	 // Generate unique order number
 269	 const orderNumber = crypto.randomUUID();
 270	
 271	 // Create order and order items in transaction
 272	 const result = await prisma.$transaction(async (tx) => {
 273	 // Create order
 274	 const order = await tx.order.create({
 275	 data: {
 276	 CustomerId,
 277	 SupplierId,
 278	 SubTotal: subTotal,
 279	 DeliveryFees,
 280	 Discount,
 281	 Notes,
 282	 PaymentMethod,
 283	 OrderNumber: orderNumber,
 284	 Status: 0, // pending
 285	 Deleted: false,
 286	 CreatedDate: new Date(),
 287	 UpdatedDate: new Date()
 288	 }
 289	 });
 290	
 291	 // Create order items
 292	 const orderItems = await Promise.all(
 293	 items.map(async (item, index) => {
 294	 const orderItem = await tx.orderItem.create({
 295	 data: {
 296	 OrderId: order.ID,
 297	 ProductId: item.ProductId,
 298	 Quantity: item.Quantity,
 299	 Deleted: false,
 300	 CreatedDate: new Date(),
 301	 UpdatedDate: new Date()
 302	 }
 303	 });
 304	
 305	 // Update product stock
 306	 await tx.products.update({
 307	 where: { ID: item.ProductId },
 308	 data: {
 309	 Stock: {
 310	 decrement: item.Quantity
 311	 },
 312	 UpdatedDate: new Date()
 313	 }
 314	 });
 315	
 316	 return orderItem;
 317	 })
 318	 );
 319	
 320	 return { order, orderItems };
 321	 });
 322	
 323	 // Return the created order with full details
 324	 return await getOrderByIdService(result.order.ID);
 325	};
