 330	
 331	/**
 332	 * Delete supplier (hard delete with transaction to handle all foreign key constraints)
 333	 */
 334	export const deleteSupplierService = async (supplierId) => {
 335	 // Check if supplier exists (using raw query to avoid any filtering)
 336	 const existingSupplier = await prisma.users.findUnique({
 337	 where: { Id: supplierId },
 338	 include: { Suppliers: true }
 339	 });
 340	
 341	 if (!existingSupplier || !existingSupplier.Suppliers) {
 342	 throw new Error('Supplier not found');
 343	 }
 344	
 345	 // Get counts of related data for reporting
 346	 const [productCount, orderCount, notificationCount, paymentMethodCount, rateCount] = await Promise.all([
 347	 prisma.products.count({ where: { SupplierId: supplierId } }),
 348	 prisma.order.count({ where: { SupplierId: supplierId } }),
 349	 prisma.notification.count({ where: { SupplierId: supplierId } }),
 350	 prisma.paymentMethods.count({ where: { SupplierId: supplierId } }),
 351	 prisma.rate.count({ where: { SupplierId: supplierId } })
 352	 ]);
 353	
 354	 // Execute transaction to handle all foreign key constraints
 355	 await prisma.$transaction(async (tx) => {
 356	 // Step 1: Handle Products - Remove supplier reference (make them unassigned)
 357	 if (productCount > 0) {
 358	 await tx.products.updateMany({
 359	 where: { SupplierId: supplierId },
 360	 data: {
 361	 SupplierId: null, // Remove supplier reference
 362	 UpdatedDate: new Date()
 363	 }
 364	 });
 365	 }
 366	
 367	 // Step 2: Handle Orders - Cancel pending orders, leave completed ones as-is
 368	 if (orderCount > 0) {
 369	 await tx.order.updateMany({
 370	 where: {
 371	 SupplierId: supplierId,
 372	 Status: { in: [0, 1] } // Only pending (0) and processing (1) orders
 373	 },
 374	 data: {
 375	 Status: 5, // Set to cancelled status
 376	 UpdatedDate: new Date()
 377	 }
 378	 });
 379	 }
 380	
 381	 // Step 3: Handle Notifications - Soft delete them
 382	 if (notificationCount > 0) {
 383	 await tx.notification.updateMany({
 384	 where: { SupplierId: supplierId },
 385	 data: {
 386	 Deleted: true,
 387	 UpdatedDate: new Date()
 388	 }
 389	 });
 390	 }
 391	
 392	 // Step 4: Handle PaymentMethods - Remove supplier reference
 393	 if (paymentMethodCount > 0) {
 394	 await tx.paymentMethods.updateMany({
 395	 where: { SupplierId: supplierId },
 396	 data: {
 397	 SupplierId: null, // Remove supplier reference
 398	 UpdatedDate: new Date()
 399	 }
 400	 });
 401	 }
 402	
 403	 // Step 5: Handle Rates - Soft delete them
 404	 if (rateCount > 0) {
 405	 await tx.rate.updateMany({
 406	 where: { SupplierId: supplierId },
 407	 data: {
 408	 Deleted: true,
 409	 UpdatedDate: new Date()
 410	 }
 411	 });
 412	 }
 413	
 414	 // Step 6: Now safely delete the supplier record
 415	 await tx.suppliers.delete({
 416	 where: { Id: supplierId }
 417	 });
 418	
 419	 // Step 7: Delete the user account (this will cascade delete related user data)
 420	 await tx.users.delete({
 421	 where: { Id: supplierId }
 422	 });
 423	 });
 424	
 425	 return {
 426	 success: true,
 427	 deletedData: {
 428	 productsUpdated: productCount,
 429	 ordersUpdated: orderCount,
 430	 notificationsDeleted: notificationCount,
 431	 paymentMethodsUpdated: paymentMethodCount,
 432	 ratesDeleted: rateCount
 433	 }
 434	 };
 435	};
 436	
Total lines in file: 436
